$(document).ready(function(){$("a").click(function(o){var t=$(this).attr("href"),i=$(t).offset();$("html, body").stop().animate({scrollTop:i.top},500),o.preventDefault()}),$.fn.isInViewport=function(){var o=$(this).offset().top,t=o+$(this).outerHeight(),i=$(window).scrollTop(),n=i+$(window).height();return i<t&&o<n},$(window).on("resize scroll",function(){$("#aboutUs2").isInViewport()||$("#advisors2").isInViewport()?$("a").css("color","#12202d"):$("a").css("color","white")})});